mapper.insert:
INSERT INTO NOTICE(    NOT_ID 
					    , NOT_WRITER 	   					    					        
						, NOT_PERMIT
					    , NOT_TITLE 
					    , NOT_CONTENTS
					    , NOT_WRITEDATE

					    	,NOT_FILE_NAME

					      )
              VALUES(  NOTICE_SEQ.NEXTVAL 
              		  , #{not_writer} 
              		  , #{not_permit}
              		  , #{not_title}
              		  , #{not_contents}
              		  , SYSDATE

              		  	,#{not_file_name}

              		  )

mapper.update:
UPDATE NOTICE
   SET NOT_COUNT = NOT_COUNT+1
 WHERE NOT_ID = #{NOT_ID}

UPDATE NOTICE N
SET N.REPLY_COUNT = (SELECT COUNT(REPLY_ID) FROM NOT_REPLY WHERE N_ID = #{n_id})
	WHERE N.NOT_ID = #{not_id}
