mapper.insert:
INSERT INTO NOTICE(    NOT_ID 
					    , NOT_WRITER 	   					    					        
						, NOT_PERMIT
					    , NOT_TITLE 
					    , NOT_CONTENTS
					    , NOT_WRITEDATE
					      )
              VALUES(  NOTICE_SEQ.NEXTVAL 
              		  , #{not_writer} 
              		  , #{not_permit}
              		  , #{not_title}
              		  , #{not_contents}
              		  , SYSDATE
              		  )

mapper.update:
UPDATE NOTICE
   SET 
   	   NOT_PERMIT = #{not_permit},
   	   NOT_TITLE = #{not_title},
  	   NOT_CONTENTS = #{not_contents}
 WHERE NOT_ID = #{not_id}

UPDATE NOTICE
   SET NOT_COUNT = NOT_COUNT+1
 WHERE NOT_ID = #{NOT_ID}
