mapper.insert:
   INSERT INTO PARENTS_BOARD(
     PAB_ID 
   , PAB_WRITER
   , PAB_TITLE
   , PAB_CONTENTS
   , PAB_CATEGORY
   , PAB_DATE
   , PAB_COUNT 
   )
	VALUES(
	  PARENTS_BOARD_SEQ.NEXTVAL 
	, #{pab_writer} 
	, #{pab_title}
	, #{pab_contents}
	, #{pab_category}
	, SYSDATE
	, #{pab_Count} - 1
	)

mapper.update:
UPDATE PARENTS_BOARD
   SET PAB_COUNT = #{pab_Count}
 WHERE PAB_ID = #{pab_id}
